mapper.update:
update drt_shop_config set
config_key=#{configKey}, config_value=#{configValue}, config_describe=#{configDescribe}, config_status=#{configStatus}, is_delete=#{isDelete}, creator_name=#{creatorName}, creator_id=#{creatorId}, create_time=#{createTime}, last_modifier_name=#{lastModifierName}, last_modifier_id=#{lastModifierId}, last_modify_time=#{lastModifyTime}
where id = #{id}
