mapper.select:
SELECT MIN(e.EARNINGS) earningsMin, MAX(e.EARNINGS) earningsMax,SUM(e.EARNINGS) earningsSum
FROM drt_fin_po_earnings_record e
WHERE #{todayTime} > e.TIME  AND e.TIME >= #{yesterdayTime}

SELECT MIN(F.EARNINGS) earningsMin, MAX(F.EARNINGS) earningsMax,SUM(F.EARNINGS) earningsSum
FROM drt_fin_other_earnings_record F
WHERE F.FROM_TYPE= #{type} AND #{todayTime} > F.TIME  AND F.TIME >= #{yesterdayTime}

    	SELECT MIN(F.order_earnings) earningsMin, MAX(F.order_earnings) earningsMax, SUM(F.order_earnings) earningsSum
		FROM drt_shop_order F
    	WHERE #{todayTime} > F.create_time  AND F.create_time >= #{yesterdayTime}
