mapper.update:
update drt_account set
CREATE_TIME=#{createTime}, USER_ID=#{userId}, PHONE=#{phone}, PWD=#{pwd}, GESTURE_PWD=#{gesturePwd}, ACCOUNT_STATUS=#{accountStatus}, IMG_URL=#{imgUrl}, NICKNAME=#{nickname}, LOGIN_FAIL_CNT=#{loginFailCnt}, LOGIN_FAIL_TIME=#{loginFailTime}, REMARK=#{remark}, GESTURE_FAIL_CNT=#{gestureFailCnt}, GESTURE_FAIL_TIME=#{gestureFailTime}, OPEN_SYS_MSG=#{openSysMsg}, OPEN_SYS_MSG_TIME=#{openSysMsgTime}, OPEN_ELE_BILL=#{openEleBill}, SOURCE=#{source}, OPEN_ELE_BILL_TIME=#{openEleBillTime}, TOTAL_EARNINGS=#{totalEarnings}, PAY_PWD=#{payPwd}
where ACCOUNT_ID = #{accountId}
