mapper.update:
update drt_opr_points_rule set
points_rule=#{pointsRule}, points_rule_type=#{pointsRuleType}, grade=#{grade}, money_scope_min=#{moneyScopeMin}, money_scope_max=#{moneyScopeMax}, bonus_column=#{bonusColumn}, points_datum_rate=#{pointsDatumRate}, coefficient=#{coefficient}, day_income=#{dayIncome}, achieve_customer_cost=#{achieveCustomerCost}, status=#{status}, remark=#{remark}, creator_id=#{creatorId}, creator_name=#{creatorName}, create_time=#{createTime}, last_modifier_id=#{lastModifierId}, last_modifier_name=#{lastModifierName}, last_modify_time=#{lastModifyTime}, is_delete=#{isDelete}
where points_rule_id = #{pointsRuleId}

mapper.select:

    select 

    from drt_opr_points_rule 
    where 1=1 

    and money_scope_min<=#{poEarningsComputingMoney} 
    and #{poEarningsComputingMoney}<=money_scope_max 

    and points_rule_type=#{pointsRuleType}

		and status=#{status}


		and is_delete=#{isDelete}

    limit 1
