mapper.update:
update drt_monitor_points_day set
present_account_num=#{presentAccountNum}, present_date=#{presentDate}, consume_earnings=#{consumeEarnings}, consume_earnings_max=#{consumeEarningsMax}, consume_earnings_min=#{consumeEarningsMin}, consume_level1_week_num=#{consumeLevel1WeekNum}, consume_level2_week_num=#{consumeLevel2WeekNum}, consume_level3_week_num=#{consumeLevel3WeekNum}, consume_level4_week_num=#{consumeLevel4WeekNum}, proceeds_earnings=#{proceedsEarnings}, proceeds_earnings_max=#{proceedsEarningsMax}, proceeds_earnings_min=#{proceedsEarningsMin}, proceeds_level1_week_num=#{proceedsLevel1WeekNum}, proceeds_level2_week_num=#{proceedsLevel2WeekNum}, proceeds_level3_week_num=#{proceedsLevel3WeekNum}, proceeds_level4_week_num=#{proceedsLevel4WeekNum}, spread_earnings=#{spreadEarnings}, spread_earnings_max=#{spreadEarningsMax}, spread_earnings_min=#{spreadEarningsMin}, spread_level1_week_num=#{spreadLevel1WeekNum}, spread_level2_week_num=#{spreadLevel2WeekNum}, spread_level3_week_num=#{spreadLevel3WeekNum}, spread_level4_week_num=#{spreadLevel4WeekNum}, payment_earnings=#{paymentEarnings}, payment_earnings_max=#{paymentEarningsMax}, payment_earnings_min=#{paymentEarningsMin}, payment_level1_week_num=#{paymentLevel1WeekNum}, payment_level2_week_num=#{paymentLevel2WeekNum}, payment_level3_week_num=#{paymentLevel3WeekNum}, payment_level4_week_num=#{paymentLevel4WeekNum}, remain_level1_week_num=#{remainLevel1WeekNum}, remain_level2_week_num=#{remainLevel2WeekNum}, remain_level3_week_num=#{remainLevel3WeekNum}, remain_level4_week_num=#{remainLevel4WeekNum}, remain_earnings=#{remainEarnings}, remain_earnings_max=#{remainEarningsMax}, remain_earnings_min=#{remainEarningsMin}
where id = #{id}

mapper.select:
SELECT MIN(F.EARNINGS) earningsMin, MAX(F.EARNINGS) earningsMax,SUM(F.EARNINGS) earningsSum
FROM drt_fin_all_earnings_record F
WHERE F.FROM_TYPE= #{type} AND #{todayTime} > F.RECORD_TIME  AND F.RECORD_TIME >= #{yesterdayTime}

    	SELECT MIN(F.order_earnings) earningsMin, MAX(F.order_earnings) earningsMax, SUM(F.order_earnings) earningsSum
		FROM drt_shop_order F
    	WHERE #{todayTime} > F.create_time  AND F.create_time >= #{yesterdayTime}
